mapper.delete:
DELETE
FROM
  advert_info
WHERE
  adverter_code = #{adverterCode}
  AND app_code = #{appCode}

mapper.update:
UPDATE
  advert_info
SET
  adverter_code=#{adverterCode}
  ,app_code=#{appCode}
  ,adverter_name=#{adverterName}
  ,balance_ratio=#{balanceRatio}
  ,come_from=#{comeFrom}
WHERE
  adverter_code = #{adverterCodeOld}
  AND app_code = #{appCodeOld}
